    tb_user_data data
SET
    data.user_money = #{userMoney}
    , data.time_ticket_end_date = #{timeTicketEndDate}

    , data.user_ticket = #{userTicket}


    , data.user_select_char = #{userSelectChar}

WHERE
    data.user_no = #{userNo}

UPDATE
    tb_user_data data
SET
    data.user_ticket = #{ticketCount}
WHERE
    data.user_no = #{userNo}
